package problems;

import static org.junit.Assert.assertEquals;
import static org.junit.Assert.assertThrows;

import org.junit.Test;

public class StringDiffTest {
    @Test
    public void testFindTheDifference() {
        assertEquals('t', (new StringDiff()).findTheDifference(
                "",
                ""));
        assertEquals('0', (new StringDiff()).findTheDifference("31654", "640513"));

    }
}

